mapper.select:
select lessontraining.student.student_id,lessontraining.student.student_name, lessontraining.student.age,lessontraining.student.sex,lessontraining.student.school, lessontraining.student.address,lessontraining.studentclass.studentclassName, lessontraining.college.collegeName,lessontraining.student.studentClassId
from lessontraining.student,lessontraining.studentclass,lessontraining.college where (lessontraining.studentclass.id = lessontraining.student.studentClassId and lessontraining.studentclass.collegeId = lessontraining.college.id)
and age = ${paging.map.get('age')}
and student_name like concat('%',${paging.map.get('name')},'%')
and lessontraining.college.collegeName like concat('%','${paging.map.get("collegeName")}','%')
and lessontraining.studentclass.studentclassName like concat('%','${paging.map.get("clazzName")}','%')
order by student_id
order by age
order by sex
limit ${(paging.pageNum-1)*paging.pageSize},${paging.pageSize};

select count(*) from lessontraining.student
where 1 = 1


        and age = ${paging.map.get("age")}


        and student_name like concat('%','${paging.map.get("name")}','%');

mapper.update:
update lessontraining.student
set student_name = #{student.student_name},age = #{student.age},
    sex = #{student.sex},school = #{student.school},address = #{student.address},
    deleteFlag = #{student.deleteFlag},info = #{student.info},updateTime = #{student.updateTime}
where student_id = #{student.student_id};
